using System.Collections;using System.Collections.Generic;using UnityEngine;public class WaypointController : MonoBehaviour {	// Use this for initialization	void Start () {			}		// Update is called once per frame	void Update () {			}    void OnTriggerEnter2D(Collider2D collision)    {                if (collision.gameObject.tag == "Enemy")        {            EnemyController ec = collision.gameObject.GetComponent<EnemyController>();            ec.isFacingLeft = !(ec.isFacingLeft);            ec.playerFlip();        }    }}